var slideHeaderIndex = 1;
showHeaderSlides(slideHeaderIndex);
autoHeaderSlider();

function currentHeaderSlide(n) {
    showHeaderSlides(slideHeaderIndex = n);
}

function plusHeaderSlide() {
    showHeaderSlides(slideHeaderIndex += 1);
}

function minusHeaderSlide() {
    showHeaderSlides(slideHeaderIndex -= 1);  
}

function showHeaderSlides(n) {
    var i;
    var slidesHeader= document.getElementsByClassName("carousel-item");
    if (n > slidesHeader.length) {
      slideHeaderIndex = 1
    }
    if (n < 1) {
        slideHeaderIndex = slidesHeader.length
    }
    for (i = 0; i < slidesHeader.length; i++) {
        slidesHeader[i].style.display = "none";
    }
    slidesHeader[slideHeaderIndex - 1].style.display = "block";
}
function autoHeaderSlider() {
var j;
var slidesHeader = document.getElementsByClassName("carousel-item");
for (j = 0; j < slidesHeader.length; j++) {
slidesHeader[j].style.display = "none";
}
slideHeaderIndex++;
if (slideHeaderIndex> slidesHeader.length) {slideHeaderIndex = 1}

slidesHeader[slideHeaderIndex-1].style.display = "block";
setTimeout(autoHeaderSlider, 3000); 
}






var slideIndex = 1;
showSlides(slideIndex);
autoSlider();

function currentSlide(n) {
    showSlides(slideIndex = n);
}

function showSlides(n) {
    var i;
    var slides= document.getElementsByClassName("service-item");
    var dots = document.getElementsByClassName("slider-dots-item");
    if (n > slides.length) {
      slideIndex = 1
    }
    if (n < 1) {
        slideIndex = slides.length
    }
    for (i = 0; i < slides.length; i++) {
        slides[i].style.display = "none";
    }
    for (i = 0; i < dots.length; i++) {
        dots[i].className = dots[i].className.replace(" active", "");
    }
    slides[slideIndex - 1].style.display = "block";
    dots[slideIndex - 1].className += " active";
}
function autoSlider() {
var j;
var slides = document.getElementsByClassName("service-item");
var dots = document.getElementsByClassName("slider-dots-item");
for (j = 0; j < slides.length; j++) {
slides[j].style.display = "none";
}
slideIndex++;
if (slideIndex> slides.length) {slideIndex = 1}
for (j = 0; j < dots.length; j++) {
dots[j].className = dots[j].className.replace(" active", "");
}
slides[slideIndex-1].style.display = "block";
dots[slideIndex-1].className += " active";
setTimeout(autoSlider, 3000); 
}






